document.getElementById('loan-form').addEventListener('submit',function(e){document.getElementById('results').style.display='none';document.getElementById('loading').style.display='block';const time=500;setTimeout(calculateResults,time);setTimeout(hideLoader,time);e.preventDefault()});function hideLoader(){document.getElementById('loading').style.display='none'}
function calculateResults(){console.log('Calculating...');const amount=document.getElementById('amount');const interest=document.getElementById('interest');const years=document.getElementById('years');const monthlyPayment=document.getElementById('monthly-payment');const totalPayment=document.getElementById('total-payment');const totalInterest=document.getElementById('total-interest');const principal=parseFloat(amount.value);const calculatedInterest=parseFloat(interest.value)/100/12;const calculatedPayments=parseFloat(years.value)*12;const x=Math.pow(1+calculatedInterest,calculatedPayments);const monthly=(principal*x*calculatedInterest)/(x-1);if(isFinite(monthly)){const fixNum=2;monthlyPayment.value=monthly.toFixed(fixNum);totalPayment.value=(monthly*calculatedPayments).toFixed(fixNum);totalInterest.value=((monthly*calculatedPayments)-principal).toFixed(fixNum);document.getElementById('results').style.display='block'}else{showError('Please check your numbers')}}
function showError(error){const errorDiv=document.createElement('div');const card=document.querySelector('.card');const heading=document.querySelector('.heading');errorDiv.className='alert alert-danger';errorDiv.appendChild(document.createTextNode(error));card.insertBefore(errorDiv,heading);setTimeout(clearError,5000)}
function clearError(){document.querySelector('.alert').remove()}